import React, { useState, useEffect } from 'react';
import './App.css';
import HabitList from './components/HabitList';
import HabitForm from './components/HabitForm';
import Login from './components/Login';
import { auth, db } from './firebase';
import { collection, query, where, getDocs, addDoc, updateDoc, doc } from 'firebase/firestore';

function App() {
  const [habits, setHabits] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        setUser(user.email);
        loadHabits(user.email);
      } else {
        setUser(null);
        setHabits([]);
      }
    });

    return () => unsubscribe();
  }, []);

  const loadHabits = async (userEmail) => {
    const habitsQuery = query(
      collection(db, 'habits'),
      where('userEmail', '==', userEmail)
    );
    const querySnapshot = await getDocs(habitsQuery);
    const habitsData = querySnapshot.docs.map(doc => ({
      id: doc.id,
      ...doc.data()
    }));
    setHabits(habitsData);
  };

  const addHabit = async (habitName) => {
    const newHabit = {
      name: habitName,
      completed: false,
      streak: 0,
      lastCompleted: null,
      userEmail: user
    };

    const docRef = await addDoc(collection(db, 'habits'), newHabit);
    setHabits([...habits, { ...newHabit, id: docRef.id }]);
  };

  const toggleHabit = async (id) => {
    const habitRef = doc(db, 'habits', id);
    const habit = habits.find(h => h.id === id);
    const today = new Date().toDateString();
    const isCompleted = !habit.completed;

    await updateDoc(habitRef, {
      completed: isCompleted,
      streak: isCompleted ? habit.streak + 1 : habit.streak - 1,
      lastCompleted: isCompleted ? today : habit.lastCompleted
    });

    setHabits(habits.map(habit => {
      if (habit.id === id) {
        return {
          ...habit,
          completed: isCompleted,
          streak: isCompleted ? habit.streak + 1 : habit.streak - 1,
          lastCompleted: isCompleted ? today : habit.lastCompleted
        };
      }
      return habit;
    }));
  };

  const handleLogout = async () => {
    await auth.signOut();
  };

  if (!user) {
    return <Login onLogin={setUser} />;
  }

  return (
    <div className="App">
      <header>
        <h1>Habit Tracker</h1>
        <div className="user-info">
          <span>Welcome, {user}!</span>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </div>
      </header>
      <HabitForm onAdd={addHabit} />
      <HabitList habits={habits} onToggle={toggleHabit} />
    </div>
  );
}

export default App;
